import React from 'react';
import './Burger.css';

class Burger extends React.Component{
  constructor(){
    super();
    this.state = {
      lettuce: 0,
      bacon: 0,
      cheese: 0,
      meat: 0
    };
  }

  // changeIngredient = ('action', 'ingredient') => {
  //   alert(action);
  // }

  render(){
    return(
      <>
        <span className="burgerDisplay">

        </span>
        <span className="burgerMaker">
          <h3>Add Ingredients</h3>
          Lettuce:
          <button onClick={() => this.changeIngredient('add', 'lettuce')} className="ingrdBtn">Add</button>
          <button onClick={() => this.changeIngredient('remove', 'lettuce')} className="ingrdBtn">Remove</button>
        </span>
      </>
    );
  }
}
export default Burger;
