import React from 'react';
import './Burger.css';

class Burger extends React.Component{
  constructor(){
    super();
    this.state = {
      lettuce: 0,
      bacon: 0,
      cheese: 0,
      meat: 0
    };
  }

  // changeIngredient = ('action', 'ingredient') => {
  //   alert(action);
  // }

  render(){
    return(
      <>
        <div className="burgerDisplay">

        </div>
        <div className="burgerMaker">
          <h3>Add Ingredients</h3>
          <div className="ingrBtns">
            <span className="btnSection">
              <h4>Lettuce:</h4>
              <button onClick={() => this.changeIngredient('add', 'lettuce')} className="ingrdBtn">Add</button>
              <button onClick={() => this.changeIngredient('remove', 'lettuce')} className="ingrdBtn">Remove</button>
            </span>
            <span>
            <h4>Bacon:</h4>
            <button onClick={() => this.changeIngredient('add', 'bacon')} className="ingrdBtn">Add</button>
            <button onClick={() => this.changeIngredient('remove', 'bacon')} className="ingrdBtn">Remove</button>
            </span>
          </div>
          <div className="ingrBtns">
            <h4>Lettuce:</h4>
            <button onClick={() => this.changeIngredient('add', 'lettuce')} className="ingrdBtn">Add</button>
            <button onClick={() => this.changeIngredient('remove', 'lettuce')} className="ingrdBtn">Remove</button>
            <h4>Bacon:</h4>
            <button onClick={() => this.changeIngredient('add', 'bacon')} className="ingrdBtn">Add</button>
            <button onClick={() => this.changeIngredient('remove', 'bacon')} className="ingrdBtn">Remove</button>
          </div>
        </div>
      </>
    );
  }
}
export default Burger;
